package me.thenameman.legendarena.commands;import me.thenameman.legendarena.core.*;import me.thenameman.legendarena.utils.*;import org.bukkit.*;import org.bukkit.command.*;import org.bukkit.entity.*;/** * @author TheNameMan */public class Firework implements CommandExecutor {    public boolean onCommand(CommandSender sender, Command command, String s, String[] args) {        if(!(sender instanceof Player)) {            sender.sendMessage("Sorry - you can only do this as a player :(");            return true;        }        if(!Rank.getRank(sender, Rank.VIP)) {            Rank.noPermissions(sender, Rank.VIP);            return true;        }        ChatUtils.shootFireworks(sender);        sender.sendMessage(ChatColor.GREEN + "Yaaay, fireworks!");        return true;    }}