package me.thenameman.legendarena.commands;import me.thenameman.legendarena.core.*;import me.thenameman.legendarena.staffchat.*;import me.thenameman.legendarena.utils.*;import org.bukkit.*;import org.bukkit.command.*;import org.bukkit.entity.*;/** * @author TheNameMan */public class Say implements CommandExecutor {    public boolean onCommand(CommandSender sender, Command command, String s, String[] args) {        String cast = ChatUtils.formatCast(args);        if(!(sender instanceof Player)) {            sender.sendMessage("Sorry - you can only do this as a player :(");            return true;        }        if(!Rank.getRank(sender, Rank.Mod)) {            Rank.noPermissions(sender, Rank.Mod);            return true;        }        if(args.length == 0) {            sender.sendMessage(PluginUtils.msgNormal + "Usage: /say <message>");        } else {            StaffChat.staffChat(SCType.ALERT, (Player) sender, ChatColor.translateAlternateColorCodes('&', cast));        }        return true;    }}