package net.thenamedev.legendapi.utils;import org.bukkit.ChatColor;import java.util.Random;/** * @author ThePixelDev */public class PluginUtils {    public static void aprilFools() {        if(!Day.getDate().isAprilFools())            return;        msgNormal = ChatColor.BLUE + "anerA dnegeL " + ChatColor.GRAY + chars[1] + " " + ChatColor.LIGHT_PURPLE;        msgError = ChatColor.BLUE + "{RRE} anerA dnegeL " + ChatColor.GRAY + chars[1] + " " + ChatColor.RED;    }    public static final char[] chars = {            '♫',            '»',            '♦',            '•',            '■',            '™',            ' ',            '↔',            '‼',            '♥',            '↨',            '►',            '→',            ' ',            '‼',            ' ',			' '    };    public static String msgNormal = ChatColor.BLUE + "Legend Arena " + ChatColor.GRAY + chars[1] + " " + ChatColor.LIGHT_PURPLE;    public static String msgError = ChatColor.BLUE + "Legend Arena {ERR}" + ChatColor.GRAY + chars[1] + " " + ChatColor.RED;    public static String msgWarning = ChatColor.BLUE + "Legend Arena {WARN} " + ChatColor.GRAY + chars[1] + " " + ChatColor.YELLOW;    public static String msgDebug = ChatColor.BLUE + "Legend Arena {DBG} " + ChatColor.GRAY + chars[1] + " " + ChatColor.GREEN;    public static boolean random(int roll, int checkFor) {        return new Random().nextInt(roll) == checkFor;    }}