package net.thenamedev.legendarena;import net.thenamedev.legendapi.song.lib.NoteBlockPlayerMain;import net.thenamedev.legendarena.utils.InitUtils;import org.bukkit.Bukkit;import org.bukkit.plugin.java.JavaPlugin;/** * @author ThePixelDev */public class LegendArena extends JavaPlugin {    public void onEnable() {        InitUtils.pluginInit();        NoteBlockPlayerMain.plugin = new NoteBlockPlayerMain();    }    public void onDisable() {        saveConfig();        Bukkit.getScheduler().cancelTasks(this);    }}