package net.thenamedev.legendarena;import net.thenamedev.legendarena.extras.staffchat.*;import net.thenamedev.legendarena.utils.*;import org.bukkit.*;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.plugin.java.*;/** * @author TheNameMan */public class LegendArena extends JavaPlugin {    private static boolean isChatMuted = false;    public static final boolean debugSwitch = true;    public static FileConfiguration config = null;    public static void toggleChatMute() {        config = Bukkit.getPluginManager().getPlugin("LegendArena").getConfig();        isChatMuted = !isChatMuted;    }    public static boolean isChatMuted() {        return isChatMuted;    }    public void onEnable() {        InitUtils.pluginInit();    }    public void onDisable() {        SCType.clearStaffchatTypes();        Bukkit.getScheduler().cancelTasks(this);    }}