package net.thenamedev.legendarena;import net.thenamedev.legendapi.tokens.*;import net.thenamedev.legendarena.extras.staffchat.*;import net.thenamedev.legendarena.utils.*;import org.bukkit.*;import org.bukkit.plugin.java.*;/** * @author TheNameMan */public class LegendArena extends JavaPlugin {    private static boolean isChatMuted = false;    public static final boolean debugSwitch = true;    public static void toggleChatMute() {        isChatMuted = !isChatMuted;    }    public static boolean isChatMuted() {        return isChatMuted;    }    public void onEnable() {        try {            InitUtils.pluginInit();        } catch(NullPointerException ignore) {}        TokenCore.init();    }    public void onDisable() {        SCType.clearStaffchatTypes();        Bukkit.getScheduler().cancelTasks(this);    }}