package net.thenamedev.legendarena;import net.thenamedev.legendarena.extras.staffchat.SCType;import net.thenamedev.legendarena.utils.InitUtils;import org.bukkit.Bukkit;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.plugin.java.JavaPlugin;/** * @author ThePixelDev */public class LegendArena extends JavaPlugin {    private static boolean isChatMuted = false;    public static FileConfiguration config = null;    public static void toggleChatMute() {        isChatMuted = !isChatMuted;    }    public static boolean isChatMuted() {        return isChatMuted;    }    public void onEnable() {        InitUtils.pluginInit();        config = Bukkit.getPluginManager().getPlugin("LegendArena").getConfig();    }    public void onDisable() {        saveConfig();        SCType.clearStaffchatTypes();        Bukkit.getScheduler().cancelTasks(this);    }}