package net.thenamedev.legendarena;import net.thenamedev.legendapi.*;import net.thenamedev.legendarena.extras.staffchat.*;import net.thenamedev.legendarena.utils.*;import org.bukkit.*;import org.bukkit.configuration.file.*;import org.bukkit.plugin.java.*;import java.util.*;/** * @author ThePixelDev */public class LegendArena extends JavaPlugin {    private static boolean isChatMuted = false;    @Deprecated    /**     * Deprecated: use LegendAPI.debug     */    public static final boolean debugSwitch = LegendAPI.debug;    public static FileConfiguration config = null;    public static final List<UUID> vanishedPlayers = new ArrayList<>();    public static void toggleChatMute() {        config = Bukkit.getPluginManager().getPlugin("LegendArena").getConfig();        isChatMuted = !isChatMuted;    }    public static boolean isChatMuted() {        return isChatMuted;    }    public void onEnable() {        InitUtils.pluginInit();    }    public void onDisable() {        SCType.clearStaffchatTypes();        Bukkit.getScheduler().cancelTasks(this);    }}