package net.thenamedev.legendarena.core;import net.thenamedev.legendarena.extras.staffchat.*;import net.thenamedev.legendarena.utils.*;import org.bukkit.*;import org.bukkit.plugin.java.*;/** * @author TheNameMan */public class LegendArena extends JavaPlugin {    private static boolean isChatMuted = false;    public static final boolean debugSwitch = false;    public static void toggleChatMute() {        isChatMuted = !isChatMuted;    }    public static boolean isChatMuted() {        return isChatMuted;    }    public void onEnable() {        try {            InitUtils.pluginInit();        } catch(NullPointerException ignore) {}        if(debugSwitch) {            ChatUtils.broadcast(ChatColor.RED + "DEBUG MODE IS ON! THIS SETTING SHOULD NOT BE TRUE IN PRODUCTION! If this is production, DISABLE THE PLUGIN, FIX IT, AND RE-ENABLE IT AFTER THE NEW JAR IS ADDED.", Rank.Dev);        }    }    public void onDisable() {        SCType.clearStaffchatTypes();        Bukkit.getScheduler().cancelTasks(this);    }}