package net.thenamedev.legendarena.listeners;import net.thenamedev.legendapi.core.chat.ChatSystem;import net.thenamedev.legendapi.utils.ChatUtils;import net.thenamedev.legendapi.utils.Day;import org.bukkit.ChatColor;import org.bukkit.event.EventHandler;import org.bukkit.event.EventPriority;import org.bukkit.event.Listener;import org.bukkit.event.player.PlayerJoinEvent;import org.bukkit.event.player.PlayerQuitEvent;/** * @author ThePixelDev */public class PlayerJoinListener implements Listener {    @EventHandler(priority = EventPriority.HIGHEST)    public void playerJoin(PlayerJoinEvent ev) {        ev.setJoinMessage(ChatColor.GRAY + "[" + ChatColor.GREEN + "+" + ChatColor.GRAY + "] " + ChatUtils.getFormattedName(ev.getPlayer()));    }    @EventHandler(priority = EventPriority.HIGH)    public void playerLeave(PlayerQuitEvent ev) {        ev.setQuitMessage(ChatColor.GRAY + "[" + ChatColor.RED + "-" + ChatColor.GRAY + "] " + ChatUtils.getFormattedName(ev.getPlayer()));        ChatSystem.remove(ev.getPlayer());    }}